edit.action:
// BEGIN mobiquo Mod push service
			if ($redirect_url && isset($config['mobiquo_push']))
		    {
		    	$subject = utf8_normalize_nfc(request_var('subject', '', true));
				$subject = (!$subject && $mode != 'post') ? $post_data['topic_title'] : $subject;
		        require_once($phpbb_root_path . $config['tapatalkdir'].'/push_hook.' . $phpEx);
		        switch ($mode)
		        {
		        	case 'reply':
		        		tapatalk_push_reply($data['post_id'], $post_data, $subject);
		        		break;
		        	case 'post':
		        		$post_data['topic_id'] = $data['topic_id'];
		        		tapatalk_push_newtopic($data['post_id'],$post_data,$subject);
		        		break;
		        	case 'quote':
		        		preg_match_all('/quote=&quot;(.*?)&quot;/is', $data['message'],$matches);
		        		$user_name_arr = array_unique($matches[1]);
		        		unset($matches);		        		
		        		tapatalk_push_reply($data['post_id'], $post_data, $subject);
		        		tapatalk_push_quote($data['post_id'], $post_data, $subject,$user_name_arr,'quote');
		        		break;
		        }
		        preg_match_all('/@(.*?)\\s/is', $data['message'],$matches);
		        $user_name_tag_arr = array_unique($matches[1]);
		        unset($matches);
		        tapatalk_push_quote($data['post_id'], $post_data, $subject,$user_name_tag_arr,'tag');		   
		    	unset($subject);
		    }
// END mobiquo Mod

// BEGIN mobiquo Mod push service
	if(!empty($data['msg_id']) && isset($config['mobiquo_push']))
	{
		require_once($phpbb_root_path . $config['tapatalkdir'].'/push_hook.' . $phpEx);
    	tapatalk_push_pm($user_id, $data['msg_id'], $subject);
	}
// END mobiquo Mod
